import React, { useState, useEffect } from 'react';

const ReceiptDetails = () => {
  const [receipts, setReceipts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);

    fetch('/api/receipts')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch receipts');
        }
        return response.json();
      })
      .then(data => {
        setReceipts(data);
      })
      .catch(error => {
        console.error('Error fetching receipts', error);
      })
      .finally(() => {
        setLoading(false);
      });

  }, []); // Remove 'receipts' from the dependency array

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="receipt-details-container">
      <h1>Customer Receipts</h1>
      {receipts.length === 0 ? (
        <p>No receipts found.</p>
      ) : (
        <ul className="receipts-list">
          {receipts.map(receipt => (
            <li key={receipt.id} className="receipt-item">
              <div className="receipt-info">
                <span className="customer-name">Customer: {receipt.customerName}</span>
                <span className="amount">Amount: {receipt.amount}</span>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ReceiptDetails;
